#pragma config(Sensor, S1,     MSMTRMX,        sensorI2CCustomFastSkipStates)
#pragma config(Sensor, S2,     MSRXMUX,        sensorI2CCustomFastSkipStates9V)
#pragma config(Motor,  motorA,          TrackSwitch,   tmotorNXT, PIDControl, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

