#include "mindsensors-rcxsensorsmux.h"
#include "mindsensors-rcxmotormux.h"

task main () {

  short chan1 = 0;
  short chan2 = 0;
  short chan4 = 0;
  short chan1_state = 0;
  short chan2_state = 0;
//  short chan4_state = 0;
  short power = 255;

  displayCenteredTextLine(0, "Mindsensors");
  displayCenteredBigTextLine(1, "RXMUX");
  displayCenteredTextLine(3, "Test 1");

  sleep(2000);

  // Set up channel 1 on the RCX sensor MUX to a light sensor in percentage mode
  MSRXMUXsetupChan(MSRXMUX, 1, sensorReflection, modePercentage, 1);

  // Set up channel 2 on the RCX sensor MUX to a touch sensor in raw mode
  MSRXMUXsetupChan(MSRXMUX, 2, sensorTouch, modeRaw, 1);

  // Set up channel 4 on the RCX sensor MUX to rotation sensor in rotation mode
  MSRXMUXsetupChan(MSRXMUX, 4, sensorRotation, modeRotation, 5);

  while(true) {
    // Read from the channels and display
    chan1 = MSRXMUXreadChan(MSRXMUX, 1);
    chan2 = MSRXMUXreadChan(MSRXMUX, 2);
    chan4 = MSRXMUXreadChan(MSRXMUX, 4);

    if(chan1 > 40 && chan1_state == 0) {
    	displayBigTextLine(1, "%d", chan1);
    	chan1_state = 1;
    	MSMTRMX_Control (MSMTRMX, MSMTRMX_M4, power);
  	}
  	else if(chan1 < 40 && chan1_state == 1) {
  		displayBigTextLine(1, "%d", chan1);
    	chan1_state = 0;
    	// reverse motor
  		MSMTRMX_Control (MSMTRMX, MSMTRMX_M4, -power);
  		sleep(20);
    	//brake motor
    	MSMTRMX_Brake (MSMTRMX, MSMTRMX_M4, power);
    	sleep (100);
    	MSMTRMX_Control (MSMTRMX, MSMTRMX_M4, 0);
  	}
  	if(chan2 > 0 && chan2_state == 0) {
  		displayBigTextLine(3, "%d", chan2);
  		chan2_state = 1;
  		//run motor channel 4
  		MSMTRMX_Control (MSMTRMX, MSMTRMX_M1, power);
  	}
  	else if(chan2 < 1 && chan2_state == 1) {
  		displayBigTextLine(3, "%d", chan2);
  		chan2_state = 0;
  		// reverse motor
  		MSMTRMX_Control (MSMTRMX, MSMTRMX_M1, -power);
  		sleep(20);
    	//brake motor
    	MSMTRMX_Brake (MSMTRMX, MSMTRMX_M1, power);
    	sleep (100);
    	MSMTRMX_Control (MSMTRMX, MSMTRMX_M1, 0);
  	}

    //displayBigTextLine(5, "%d", chan4);
  }

  playSound(soundBeepBeep);
  while(bSoundActive) sleep(1);
  eraseDisplay();
  while(1) sleep(1);

}
